// React 19 - Component-Based UI (2025)

// PROBLEM: Vanilla JavaScript (manual DOM manipulation)
function createUserCardVanilla(name, email) {
  let card = '';
  card += '<div class="user-card">';
  card += '  <h3>' + name + '</h3>';
  card += '  <p>' + email + '</p>';
  card += '</div>';
  return card;
}

console.log('Vanilla JS:', createUserCardVanilla('Alice', 'alice@example.com'));
// Problem: String manipulation, no reactivity, error-prone

// REACT WAY: Component-based (simulated JSX)
function UserCard(props) {
  return {
    type: 'div',
    props: { className: 'user-card' },
    children: [
      { type: 'h3', children: props.name },
      { type: 'p', children: props.email }
    ]
  };
}

let userCard = UserCard({ name: 'Alice', email: 'alice@example.com' });
console.log('React component:', JSON.stringify(userCard, null, 2));

// REAL REACT 19 CONCEPTS (demonstrated conceptually)

// 1. COMPONENTS - Reusable UI pieces
console.log('\n=== React Core Concepts ===\n');

function Button(props) {
  return {
    type: 'button',
    props: {
      onClick: props.onClick,
      className: 'btn'
    },
    children: props.label
  };
}

let button1 = Button({ label: 'Click Me', onClick: () => console.log('Clicked!') });
let button2 = Button({ label: 'Submit', onClick: () => console.log('Submitted!') });

console.log('Reusable Button component created twice');

// 2. PROPS - Passing data to components
function Greeting(props) {
  return {
    type: 'h1',
    children: `Hello, ${props.name}!`
  };
}

let greeting = Greeting({ name: 'World' });
console.log('Greeting:', greeting.children); // 'Hello, World!'

// 3. STATE - Dynamic data that changes
class Counter {
  constructor() {
    this.state = { count: 0 };
  }

  increment() {
    this.state.count++;
    console.log('Count:', this.state.count);
  }

  render() {
    return {
      type: 'div',
      children: [
        { type: 'p', children: `Count: ${this.state.count}` },
        { type: 'button', props: { onClick: () => this.increment() }, children: '+1' }
      ]
    };
  }
}

let counter = new Counter();
console.log('\nCounter initial:', counter.state.count);
counter.increment(); // Count: 1
counter.increment(); // Count: 2

// 4. JSX - HTML-like syntax (compiles to JavaScript)
console.log('\nJSX Example (conceptual):');
let jsxExample = `
  <div className="app">
    <h1>Welcome to React 19</h1>
    <Button label="Click Me" onClick={handleClick} />
  </div>
`;
console.log(jsxExample);
console.log('JSX makes components look like HTML but with JavaScript power!');

// 5. VIRTUAL DOM - React's secret weapon
console.log('\nVirtual DOM Concept:');
let realDOM = ['User 1', 'User 2', 'User 3'];
let virtualDOM = ['User 1', 'User 2', 'User 3', 'User 4']; // Added User 4

console.log('React compares virtual and real DOM');
console.log('Only updates what changed (User 4) - super efficient!');

// WHY REACT?
let benefits = [
  '✓ Component-based: Reusable UI pieces',
  '✓ Declarative: Describe WHAT you want, React handles HOW',
  '✓ Fast: Virtual DOM updates only what changed',
  '✓ Popular: Huge ecosystem, massive community',
  '✓ React 19: Server Components, new hooks, automatic optimizations',
  '✓ Learn once, write anywhere: Web, mobile (React Native), desktop'
];

console.log('\nReact 19 Benefits:');
benefits.forEach(b => console.log(b));

