// Create a card component
function createCard(title, description) {
  return {
    type: 'div',
    className: 'card',
    children: [
      { type: 'h2', children: title },
      { type: 'p', children: description }
    ]
  };
}

// Create a list component
function createList(items) {
  return {
    type: 'ul',
    children: items.map(item => ({
      type: 'li',
      children: item
    }))
  };
}

// Test
let card = createCard('My Card', 'This is a card description');
console.log('Card:', JSON.stringify(card, null, 2));

let list = createList(['Item 1', 'Item 2', 'Item 3']);
console.log('List:', JSON.stringify(list, null, 2));

lessons.solution:
// Complete React-style component system

// Card component
function createCard(title, description) {
  return {
    type: 'div',
    className: 'card',
    children: [
      { type: 'h2', children: title },
      { type: 'p', children: description }
    ]
  };
}

// List component
function createList(items) {
  return {
    type: 'ul',
    className: 'list',
    children: items.map(item => ({
      type: 'li',
      children: item
    }))
  };
}

// Button component
function createButton(label, onClick) {
  return {
    type: 'button',
    props: { onClick },
    children: label
  };
}

// Container component (composition)
function createApp() {
  return {
    type: 'div',
    className: 'app',
    children: [
      createCard('Welcome', 'This is a React-style app'),
      createList(['Feature 1', 'Feature 2', 'Feature 3']),
      createButton('Click Me', () => console.log('Clicked!'))
    ]
  };
}

// Test all components
console.log('=== React Component System ===\n');

let card = createCard('My Card', 'Card description here');
console.log('Card component:');
console.log(JSON.stringify(card, null, 2));

let list = createList(['React', 'Vue', 'Angular']);
console.log('\nList component:');
console.log(JSON.stringify(list, null, 2));

let app = createApp();
console.log('\nComplete App:');
console.log(JSON.stringify(app, null, 2));

lessons.commonStickingPoints:
Common React beginner mistakes:

1. **Forgetting to import React** (in older versions):
   ```javascript
   // React 17 and earlier - needed
   import React from 'react';

   // React 19 - automatic JSX transform, no import needed!
   // Just write JSX directly
   ```

2. **Trying to mutate state directly**:
   ```javascript
   // Wrong!
   let [count, setCount] = useState(0);
   count = count + 1; // Doesn't trigger re-render!

   // Correct!
   setCount(count + 1); // Triggers re-render
   ```

3. **Not using keys in lists**:
   ```javascript
   // Wrong!
   items.map(item => <li>{item}</li>)

   // Correct!
   items.map((item, index) => <li key={index}>{item}</li>)
   // Better: use unique ID if available
   items.map(item => <li key={item.id}>{item.name}</li>)
   ```

4. **className vs class**:
   ```javascript
   // Wrong! (class is a JavaScript keyword)
   <div class="card">...</div>

   // Correct!
   <div className="card">...</div>
   ```

5. **Inline styles syntax**:
   ```javascript
   // Wrong!
   <div style="color: red">...</div>

   // Correct! (object with camelCase)
   <div style={{ color: 'red', fontSize: '16px' }}>...</div>
   ```

6. **Event handler syntax**:
   ```javascript
   // Wrong!
   <button onClick={handleClick()}>Click</button> // Calls immediately!

   // Correct!
   <button onClick={handleClick}>Click</button> // Passes function
   <button onClick={() => handleClick()}>Click</button> // Arrow function
   ```

7. **JSX must return single element**:
   ```javascript
   // Wrong!
   return (
     <h1>Title</h1>
     <p>Text</p>
   );

   // Correct! (wrap in div or Fragment)
   return (
     <div>
       <h1>Title</h1>
       <p>Text</p>
     </div>
   );

   // Or use Fragment
   return (
     <>
       <h1>Title</h1>
       <p>Text</p>
     </>
   );
   ```
